    isAddressUpdateSubmit,
    Set(
        displayedAddressLine1Error,
        cachedAddressLine1Error
    ) & Set(
        displayedAddressLine2Error,
        cachedAddressLine2Error
    ) & Set(
        displayedTownCityError,
        cachedTownCityError
