        isDateBlank,
        Set(
            cacheErrorMsg,
            "Invalid date"
        ),
        isDayInvalid && isMonthInvalid && isYearInvalid,
        Set(
            cacheErrorMsg,
            "Day, month and year are invalid"
        ),
        isDayInvalid && isMonthInvalid,
        Set(
            cacheErrorMsg,
            "Day and month are invalid"
        ),
        isDayInvalid && isYearInvalid,
        Set(
            cacheErrorMsg,
            "Day and year are invalid"
        ),
        isMonthInvalid && isYearInvalid,
        Set(
            cacheErrorMsg,
            "Month and year are invalid"
        ),
        isMonthInvalid,
        Set(
            cacheErrorMsg,
            "Month is invalid"
        ),
        isDayInvalid,
        Set(
            cacheErrorMsg,
            "Day is invalid"
        ),
        isYearInvalid,
        Set(
            cacheErrorMsg,
            "Year is invalid"
        ),
        IsMaxTodayError,
        Set(
            cacheErrorMsg,
            "Date must be today or in the past"
        ),
        IsMinTodayError,
        Set(
            cacheErrorMsg,
            "Date must be today or in the future"
        ),
        IsBeforeError,
        Set(
            cacheErrorMsg,
            "Date must be the same as or after " & Text(
                DateValue(CompDate.MinDate),
                CompDate.InternalDateFormat
            )
        ),
        IsAfterError,
        Set(
            cacheErrorMsg,
            "Date must be the same as or before " & Text(
                DateValue(CompDate.MaxDate),
                CompDate.InternalDateFormat
            )
        ),
        IsBetweenError,
        Set(
            cacheErrorMsg,
            "Date must be between " & Text(
                DateValue(CompDate.MinDate),
                CompDate.InternalDateFormat
            ) & " and " & Text(
                DateValue(CompDate.MaxDate),
                CompDate.InternalDateFormat
            )
        )
    );
    If(
        !CompDate.IsOnSubmitEnabled,
        Set(
            errorMessage,
            cacheErrorMsg
        )
    )
)

Set(isYearSelected, true);
Set(isDateSelected, (!CompDate.IsDayEnabled || isDaySelected) && (isMonthSelected) && (!CompDate.IsYearEnabled || isYearSelected));
Set(isDirty, isDateSelected && !CompDate.IsOptional);
