Rules.InvariantScript:
Table(
    {
        cat_name: "PrimaryColor1Value",
        cat_value: "#ffffff"
    },
    {
        cat_name: "PrimaryColor1TextValue",
        cat_value: "#00733c"
    },
    {
        cat_name: "BackgroundColorValue",
        cat_value: "#ffffff"
    },
    {
        cat_name: "BorderColorValue",
        cat_value: "#093916"
    },
    {
        cat_name: "BorderThicknessValue",
        cat_value: 3
    },
    {
        cat_name: "defaultLabelColorValue",
        cat_value: "#121212"
    },
    {
        cat_name: "FocusedBorderThicknessValue",
        cat_value: 4
    },
    {
        cat_name: "PaddingValue",
        cat_value: 20
    },
    {
        cat_name: "PrimaryColor2Value",
        cat_value: "#093916"
    },
    {
        cat_name: "PrimaryColor2TextValue",
        cat_value: "#ffffff"
    },
    {
        cat_name: "PrimaryColor3Value",
        cat_value: "#f2001c"
    },
    {
        cat_name: "PrimaryColor3TextValue",
        cat_value: "#f2001c"
    },
    {
        cat_name: "PrimaryColor4Value",
        cat_value: "#0c5424"
    },
    {
        cat_name: "RadiusValue",
        cat_value: 3
    },
    {
        cat_name: "TextBodyFontFaceValue",
        cat_value: "Arial"
    },
    {
        cat_name: "TextBodyFontSizePhoneValue",
        cat_value: 12
    },
    {
        cat_name: "TextBodyFontSizeValue",
        cat_value: 14
    },
    {
        cat_name: "TextEmphasisFontSizePhoneValue",
        cat_value: 14
    },
    {
        cat_name: "TextEmphasisFontSizeValue",
        cat_value: 18
    }
)

// It is counted as a submit update if the Submit value has been changed (true -> false OR false -> true)
Set(isTNUpdateSubmit, Self.Submit <> previousTNSubmitValue);

// OnSubmit
If(
    isTNUpdateSubmit,
    Set(
        displayedTNError,
        cachedTNError
    )
);

// OnReset
// We reset if this function was called by anything but Submit being toggled true or false
If(
    !isTNUpdateSubmit,
    Set(
        displayedTNError,
        Blank()
    ) & Set(
        cachedTNError,
        Blank()
    ) & Reset(txtTNInput)
);

Set(previousTNSubmitValue, Self.Submit);

Max(
    If(
        !IsNumeric(
            LookUp(
                TelephoneNumber.Theme,
                cat_name = "TextEmphasisFontSizeValue"
            ).cat_value
        ) || IsBlankOrError(
            LookUp(
                TelephoneNumber.Theme,
                cat_name = "TextEmphasisFontSizeValue"
            ).cat_value
        ),
        18,
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "TextEmphasisFontSizeValue"
        ).cat_value
    ),
    1
)

// Initialise telephone number error
Set(
    cachedTNError,
    Blank()
);

// Checks if text input has been left blank
Set(
    isBlank,
    Len(
        Concat(
            MatchAll(
                Self.Text,
                Match.MultipleDigits
            ),
            FullMatch
        )
    ) = 0
);

// Checks if user has entered a valid telephone number
Set(
    isInvalid,
    Len(
        Concat(
            MatchAll(
                Self.Text,
                Match.MultipleDigits
            ),
            FullMatch
        )
    ) < 6 || Len(
        Concat(
            MatchAll(
                Self.Text,
                Match.MultipleDigits
            ),
            FullMatch
        )
    ) > 15
);

// Changes error message text based on condition met
Switch(
    true,
    isBlank && TelephoneNumber.IsOptional,
    Set(
        cachedTNError,
        Blank()
    ),
    isBlank && !TelephoneNumber.IsOptional,
    Set(
        cachedTNError,
        "Enter telephone number"
    ),
    isInvalid,
    Set(
        cachedTNError,
        "Enter a valid telephone number"
    ),
    Set(
        cachedTNError,
        Blank()
    )
);

// If we want live updates of Errors, then we automatically update the displayed error
If(
    !TelephoneNumber.IsOnSubmitEnabled,
    Set(
        displayedTNError,
        cachedTNError
    )
);

"data:image/svg+xml;utf8, " & EncodeUrl(
    $"<svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' fill='{If(
        IsBlankOrError(
            ColorValue(
                LookUp(
                    TelephoneNumber.Theme,
                    cat_name = "PrimaryColor4Value"
                ).cat_value
            )
        ),
        "#808080",
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "PrimaryColor4Value"
        ).cat_value
    )}' class='bi bi-telephone-fill' viewBox='0 0 16 16'>
  <path fill-rule='evenodd' d='M1.885.511a1.745 1.745 0 0 1 2.61.163L6.29 2.98c.329.423.445.974.315 1.494l-.547 2.19a.68.68 0 0 0 .178.643l2.457 2.457a.68.68 0 0 0 .644.178l2.189-.547a1.75 1.75 0 0 1 1.494.315l2.306 1.794c.829.645.905 1.87.163 2.611l-1.034 1.034c-.74.74-1.846 1.065-2.877.702a18.6 18.6 0 0 1-7.01-4.42 18.6 18.6 0 0 1-4.42-7.009c-.362-1.03-.037-2.137.703-2.877z'/>
</svg>"
)

If(
     IsBlankOrError(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "TextBodyFontFaceValue"
        ).cat_value
    ),
   Font.'Open Sans',
     LookUp(
        TelephoneNumber.Theme,
        cat_name = "TextBodyFontFaceValue"
    ).cat_value
)

If(
    !IsNumeric(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "PaddingValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "PaddingValue"
        ).cat_value
    ),
    20,
    LookUp(
        TelephoneNumber.Theme,
        cat_name = "PaddingValue"
    ).cat_value
)

Max(
    If(
        !IsNumeric(
            LookUp(
                TelephoneNumber.Theme,
                cat_name = "TextBodyFontSizeValue"
            ).cat_value
        ) || IsBlankOrError(
            LookUp(
                TelephoneNumber.Theme,
                cat_name = "TextBodyFontSizeValue"
            ).cat_value
        ),
        16,
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "TextBodyFontSizeValue"
        ).cat_value
    ),
    1
)

If(
    !IsNumeric(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "BorderThicknessValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "BorderThicknessValue"
        ).cat_value
    ),
    2,
    LookUp(
        TelephoneNumber.Theme,
        cat_name = "BorderThicknessValue"
    ).cat_value
)

If(
    !IsNumeric(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "RadiusValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "RadiusValue"
        ).cat_value
    ),
    5,
    LookUp(
        TelephoneNumber.Theme,
        cat_name = "RadiusValue"
    ).cat_value
)

If(
     IsBlankOrError(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "TextBodyFontFaceValue"
        ).cat_value
    ),
   Font.'Open Sans',
     LookUp(
        TelephoneNumber.Theme,
        cat_name = "TextBodyFontFaceValue"
    ).cat_value
)

If(!IsBlank(displayedTNError), rctTNError.Fill, If(
    IsBlankOrError(
        ColorValue(
            LookUp(
                TelephoneNumber.Theme,
                cat_name = "BorderColorValue"
            ).cat_value
        )
    ),
    RGBA(
        0,
        18,
        107,
        1
    ),
    ColorValue(
        LookUp(
            TelephoneNumber.Theme,
            cat_name = "BorderColorValue"
        ).cat_value
    )
))
