Rules.InvariantScript:
Table(
    {
        cat_name: "PrimaryColor1Value",
        cat_value: "#ffffff"
    },
    {
        cat_name: "PrimaryColor1TextValue",
        cat_value: "#00733c"
    },
    {
        cat_name: "BackgroundColorValue",
        cat_value: "#ffffff"
    },
    {
        cat_name: "BorderColorValue",
        cat_value: "#093916"
    },
    {
        cat_name: "BorderThicknessValue",
        cat_value: 3
    },
    {
        cat_name: "defaultLabelColorValue",
        cat_value: "#121212"
    },
    {
        cat_name: "FocusedBorderThicknessValue",
        cat_value: 4
    },
    {
        cat_name: "PaddingValue",
        cat_value: 20
    },
    {
        cat_name: "PrimaryColor2Value",
        cat_value: "#093916"
    },
    {
        cat_name: "PrimaryColor2TextValue",
        cat_value: "#ffffff"
    },
    {
        cat_name: "PrimaryColor3Value",
        cat_value: "#f2001c"
    },
    {
        cat_name: "PrimaryColor3TextValue",
        cat_value: "#f2001c"
    },
    {
        cat_name: "PrimaryColor4Value",
        cat_value: "#0c5424"
    },
    {
        cat_name: "RadiusValue",
        cat_value: 3
    },
    {
        cat_name: "TextBodyFontFaceValue",
        cat_value: "Arial"
    },
    {
        cat_name: "TextBodyFontSizePhoneValue",
        cat_value: 12
    },
    {
        cat_name: "TextBodyFontSizeValue",
        cat_value: 14
    },
    {
        cat_name: "TextEmphasisFontSizePhoneValue",
        cat_value: 14
    },
    {
        cat_name: "TextEmphasisFontSizeValue",
        cat_value: 18
    }
)

// It is counted as a submit update if the Submit value has been changed (true -> false OR false -> true)
Set(isEmailUpdateSubmit, Self.Submit <> previousEmailSubmitValue);

// OnSubmit
If(
    isEmailUpdateSubmit,
    Set(
        displayedEmailError,
        cachedEmailError
    )
);

// OnReset
// We reset if this function was called by anything but Submit being toggled true or false
If(
    !isEmailUpdateSubmit ,
    Set(
        displayedEmailError,
        Blank()
    ) & Set(
        cachedEmailError,
        Blank()
    ) & Reset(txtEmailInput)
);

Set(previousEmailSubmitValue, Self.Submit);

Max(
    If(
        !IsNumeric(
            LookUp(
                Email.Theme,
                cat_name = "TextEmphasisFontSizeValue"
            ).cat_value
        ) || IsBlankOrError(
            LookUp(
                Email.Theme,
                cat_name = "TextEmphasisFontSizeValue"
            ).cat_value
        ),
        18,
        LookUp(
            Email.Theme,
            cat_name = "TextEmphasisFontSizeValue"
        ).cat_value
    ),
    1
)

//Initialize Email Error
Set(
    cachedEmailError,
    Blank()
);
// Checks user hasn't left the email input empty if it is required
If(
    IsBlank(cachedEmailError) && !Email.IsOptional && IsBlank(Trim(Self.Text)),
    Set(
        cachedEmailError,
        "Enter an email address"
    )
);
// Checks user has entered a valid email address
If(
    IsBlank(cachedEmailError) && !IsBlank(Trim(Self.Text)) && !IsMatch(
        Trim(txtEmailInput.Text),
        "^[^\s^@]+@[^\s@]+\.[^\s@]+$"
    ),
    Set(
        cachedEmailError,
        "Enter an email address in the correct format, like name@example.com"
    )
);

// if we want live updates, we automatically update the displayed error
If(
    !Email.IsOnSubmitEnabled,
    Set(
        displayedEmailError,
        cachedEmailError
    )
)

"data:image/svg+xml;utf8, " & EncodeUrl(
    $"<svg xmlns='http://www.w3.org/2000/svg' width='16' height='16' fill='{If(
        IsBlankOrError(
            ColorValue(
                LookUp(
                    Email.Theme,
                    cat_name = "PrimaryColor4Value"
                ).cat_value
            )
        ),
        "#808080",
        LookUp(
            Email.Theme,
            cat_name = "PrimaryColor4Value"
        ).cat_value
    )}' class='bi bi-envelope-fill' viewBox='0 0 16 16'>
  <path d='M.05 3.555A2 2 0 0 1 2 2h12a2 2 0 0 1 1.95 1.555L8 8.414zM0 4.697v7.104l5.803-3.558zM6.761 8.83l-6.57 4.027A2 2 0 0 0 2 14h12a2 2 0 0 0 1.808-1.144l-6.57-4.027L8 9.586zm3.436-.586L16 11.801V4.697z'/>
</svg>"
)

If(
    !IsNumeric(
        LookUp(
            Email.Theme,
            cat_name = "PaddingValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            Email.Theme,
            cat_name = "PaddingValue"
        ).cat_value
    ),
    20,
    LookUp(
        Email.Theme,
        cat_name = "PaddingValue"
    ).cat_value
)

If(
     IsBlankOrError(
        LookUp(
            Email.Theme,
            cat_name = "TextBodyFontFaceValue"
        ).cat_value
    ),
   Font.'Open Sans',
     LookUp(
        Email.Theme,
        cat_name = "TextBodyFontFaceValue"
    ).cat_value
)

Max(
    If(
        !IsNumeric(
            LookUp(
                Email.Theme,
                cat_name = "TextBodyFontSizeValue"
            ).cat_value
        ) || IsBlankOrError(
            LookUp(
                Email.Theme,
                cat_name = "TextBodyFontSizeValue"
            ).cat_value
        ),
        16,
        LookUp(
            Email.Theme,
            cat_name = "TextBodyFontSizeValue"
        ).cat_value
    ),
    1
)

If(!IsBlank(displayedEmailError), shpEmailErrorBar.Fill, If(
    IsBlankOrError(
        ColorValue(
            LookUp(
                Email.Theme,
                cat_name = "BorderColorValue"
            ).cat_value
        )
    ),
    RGBA(
        0,
        18,
        107,
        1
    ),
    ColorValue(
        LookUp(
            Email.Theme,
            cat_name = "BorderColorValue"
        ).cat_value
    )
))

If(
     IsBlankOrError(
        LookUp(
            Email.Theme,
            cat_name = "TextBodyFontFaceValue"
        ).cat_value
    ),
   Font.'Open Sans',
     LookUp(
        Email.Theme,
        cat_name = "TextBodyFontFaceValue"
    ).cat_value
)

If(
    !IsNumeric(
        LookUp(
            Email.Theme,
            cat_name = "RadiusValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            Email.Theme,
            cat_name = "RadiusValue"
        ).cat_value
    ),
    5,
    LookUp(
        Email.Theme,
        cat_name = "RadiusValue"
    ).cat_value
)

If(
    !IsNumeric(
        LookUp(
            Email.Theme,
            cat_name = "BorderThicknessValue"
        ).cat_value
    ) || IsBlankOrError(
        LookUp(
            Email.Theme,
            cat_name = "BorderThicknessValue"
        ).cat_value
    ),
    2,
    LookUp(
        Email.Theme,
        cat_name = "BorderThicknessValue"
    ).cat_value
)
